Titulos_formula_mp.formato:
(dato)=> {
let cantidad = Number(`${dato.cantidad ? dato.cantidad : 0}`); 
if(Number(dato.saco)!==0 && Number(dato.cantsaco)!==0){
   cantidad= Number(dato.saco) * Number(dato.cantsaco);
}
return cantidad
}

Titulos_venta.formato:
(dato)=> {
let precio = dato.precio ? Number(dato.precio) : 0;
let cantidad = dato.cantidad ? Number(dato.cantidad) : 1;
let total = precio * cantidad;
return Number(`${total}`)
}
